import { useState, useEffect } from "react";
import { useFormik } from "formik";
import toast from "react-hot-toast";
import { object, string } from "yup";
import { useNavigate } from "react-router-dom";

function FindRecipe() {
  const nav = useNavigate();

  const [ingFields, setIngFields] = useState(2);

  const recipeSchema = object({
    name: string(),
    steps: string(),
  });

  const initialValues = {
    name: "",
    steps: "",
  };

  const formik = useFormik({
    initialValues,
    validationSchema: recipeSchema,
    onSubmit: (formData) => {
      console.log(formData);
    },
  });

  const addField = () => {
    setIngFields((ingFields) => ingFields + 1);
  };

  useEffect(() => {}, [ingFields]);

  return (
    <div className="p-6 flex flex-row">
      <div className="bg-shittake text-white p-6 rounded-lg">
        <h2 className="text-2xl">Enter Ingredients</h2>

        <form className="flex flex-col mt-2" onSubmit={formik.handleSubmit}>
          <label htmlFor="name">Ingredients</label>

          <button type="submit">Search</button>
        </form>

        <button onClick={addField}>Add +</button>
      </div>

      <div className="border-1">
        <h1>RESULTS</h1>
      </div>
    </div>
  );
}

export default FindRecipe;
