								/>
								{formik.errors.ingredients && formik.touched.ingredients && (
									<div className='error-message show'>{formik.errors.ingredients}</div>
								)} */}

								<button className='bg-shittake text-white rounded-lg m-1 p-1'>
									Update Profile
								</button>
						</form>


					</div>

                    : 

                    <></>

                }

        </div>

    )
}

export default Profile
