    const handleEdit = () => {
        console.log("EDIT")
        setEditMode(!editMode)
    }

    const handleDelete = () => {
        console.log("DELETE")
    }

    return (

        <div className='text-lg bg-green-200 p-6 flex flex-col'>

            <h1 className='text-4xl font-bold'>Profile</h1>

            <h1 className=''>{user.username}</h1>
            <h3 className=''>{user.email}</h3>

            <p># of Saved Recipes: {user.recipes.length}</p>

            <button className=' bg-white text-green-500 rounded-lg hover:text-black p-1' onClick={handleEdit}>EDIT</button>

            <button className=' bg-white text-red-500 rounded-lg hover:text-black p-1 ' onClick={handleDelete}>DELETE ACCOUNT</button>

        </div>

    )
}

export default Profile
