import { useFormik } from 'formik'
import toast from 'react-hot-toast'
import { object, string } from 'yup'
import { useNavigate } from 'react-router-dom'

function FindRecipe() {
    const nav = useNavigate()

    const recipeSchema = object({
        name: string(),
        steps: string()
    })

    const initialValues = {
        name: '',
        steps: ''
    }

    const formik = useFormik({
		initialValues,
		validationSchema: recipeSchema,
		onSubmit: (formData) => {
			fetch('/entries', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json'
				},
				body: JSON.stringify({
					name: formData.name
				})
			})
				.then((res) => {
					if (res.ok) {
						return res.json().then((data) => {


							nav('/cookbook')
							toast.success("Entry Submited")
						})
					} else {
						return res
							.json()
							.then((errorObj) => toast.error(errorObj.message))
					}
				})
		}
	})

    return (
        <div className='p-6 flex flex-row'>

            <div className='bg-shittake text-white p-6'>
                <form className='flex flex-col'>

                    <label htmlFor='name'>Ingredients</label>

                        <input
                            type='text'
                            name='ingredient'
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            value={formik.values.ingredient}
                            id = 'name'
                            placeholder='Ingredient'
                        />
                        {formik.errors.ingredient && formik.touched.ingredient && (
                            <div className='error-message show'>{formik.errors.ingredient}</div>
                        )}


                </form>

            </div>

            <div className='border-1'>
                <h1>RESULTS</h1>


            </div>




        </div>
    )
}

export default FindRecipe
