import { useState, useEffect } from "react";
import { useFormik, FieldArray, Formik, Field } from "formik";
import toast from "react-hot-toast";
import { object, string, array } from "yup";
import { useNavigate } from "react-router-dom";

function FindRecipe() {
  const nav = useNavigate();

  const [ingFields, setIngFields] = useState(2);

  const ingredientSearchSchema = object();

  const initialValues = {
    settings: "aksdjf",
    erdfw: "kdfjlsdkfj",
    ingredients: [
      {
        name: "Christian",
        amount: "123",
        measurement: "1232",
      },
    ],
  };

  const formik = useFormik({
    initialValues,
    validationSchema: ingredientSearchSchema,
    onSubmit: (formData) => {
      console.log(formData);
    },
  });

  return (
    <div className="p-6 flex flex-row">
      <div className="bg-shittake text-white p-6 rounded-lg">
        <h2 className="text-2xl">Enter Ingredients</h2>

        <Formik initialValues={initialValues} onSubmit={formik.handleSubmit}>
          <label htmlFor="name">Ingredients</label>

          <FieldArray name="ingredients" validateOnChange={false}>
            {(fieldArrayProps) => {
              const { push, remove, form } = fieldArrayProps;
              const { values } = form;
              const ingredients = values.ingredients || [];

              const handleAddIngredient = () => {
                push({ name: "", amount: "", measurement: "" });
              };

              return (
                <div>
                  {ingredients.map((ingredient, index) => (
                    <div key={index}>
                      <Field
                        name={`ingredients[${index}].name`}
                        placeholder="Name"
                        className="m-1 p-1 rounded-lg"
                      />
                      <Field
                        name={`ingredients[${index}].amount`}
                        placeholder="Amount"
                        className="m-1 p-1 rounded-lg"
                      />
                      <Field
                        name={`ingredients[${index}].measurement`}
                        placeholder="Measurement"
                        className="m-1 p-1 rounded-lg"
                      />

                      <button
                        type="button"
                        onClick={() => remove(index)}
                        className="p-1 m-1 bg-champagne text-black rounded-lg"
                      >
                        REMOVE
                      </button>
                      <button
                        type="button"
                        onClick={handleAddIngredient}
                        className="p-1 m-1 bg-champagne text-black rounded-lg"
                      >
                        ADD
                      </button>
                    </div>
                  ))}
                </div>
              );
            }}
          </FieldArray>

          <button className="bg-champagne text-black rounded-lg" type="submit">
            Search
          </button>
        </Formik>
      </div>

      <div className="border-1">
        <h1>RESULTS</h1>
      </div>
    </div>
  );
}

export default FindRecipe;
